import React from "react";
import {Body} from "./Body";

export default function New(){
    const [input, setInput] = React.useState({itemName: ''})
    const [items, setItems] = React.useState([])
    const [number, setNumber] = React.useState(1)

    function update(e){
        const {name, value} = e.target
        setInput({
            [name]: value
        })
    }

    function addToCart(){
        setItems((previous) => [...previous,{tag: input.itemName, init: 4}])
        setInput({
            itemName: ''
        })
    }

    function add(){
        setNumber(Old => old + 1)
    }

    const body = items.map(item => 
    <div className="item-row">
        <div className="indicate"></div>
        <div>{item.tag}</div>
        <div className="amount">
            <div className="decrease">V</div>
            <div>{number}</div>
            <div className="increase" onClick={add}>V</div>
        </div>
    </div>)


    return (
        <div>
            <div className="top">
                <input 
                    type='text'
                    value={input.itemName}
                    placeholder='Add an item'
                    name="itemName"
                    onChange={update}
                    className="input"
                />
                <span onClick={addToCart} className="list-plus">+</span>
            </div>
            {body}
        </div>
    )
}
