import React from "react";
import {Body} from "./Body";

export default function New(){
    const [input, setInput] = React.useState({itemName: ''})
    const [items, setItems] = React.useState([])
    const [number, setNumber] = React.useState(1)

    function update(e){
        const {name, value} = e.target
        setInput((old) =>  {
         return{
                [name]: value
        }})
    }

    function addToCart(){
        {input.itemName && setItems((previous) => [...previous,{tag: input.itemName, qty: 1}])
        setInput((old) => {
           return {
               ...old,
                itemName: ''
           } 
        })}


    }

    function add(index){
        //items[index].qty = items[index].qty + 1

        return items[index].qty + 1
    }


    /*function subtract(){
        setNumber(old => old = old > 1 ? old - 1: 1)
    }*/

    const body = items.map((item, index) => 
        <div className="item-row">
            <div className="indicate"></div>
            <div>{item.tag}</div>
            <div className="amount">
                <div className="decrease">V</div>
                <div>{item.qty}</div>
                <div className="increase" onClick={() => add(index)}>V</div>
            </div>
        </div>
    )


    return (
        <div>
            <div className="top">
                <input 
                    type='text'
                    value={input.itemName}
                    placeholder='Add an item'
                    name="itemName"
                    onChange={update}
                    className="input"
                />
                <span onClick={addToCart} className="list-plus">+</span>
            </div>
            {body}
        </div>
    )
}
