import React from "react";
import {Body} from "./Body";

export default function New(){
    const [input, setInput] = React.useState({itemName: ''})
    const [items, setItems] = React.useState([])
    const [number, setNumber] = React.useState(3)

    function update(e){
        const {name, value} = e.target
        setInput((old) =>  {
         return{
                [name]: value
        }})
    }

    function addToCart(){
        {input.itemName && setItems((previous) => [...previous,{tag: input.itemName, currentValue: 2}])
        setInput((old) => {
           return {
                itemName: '',
           } 
        })}


    }

    function add(){
        items.currentValue = items.currentValue + 1
    }

    function add(){
        alert(number)
    }


    function subtract(){
        setNumber(old => old = old > 1 ? old - 1: 1)
    }

    const body = items.map(item => 
        <div className="item-row">
            <div className="indicate"></div>
            <div>{item.tag}</div>
            <div className="amount">
                <div className="decrease" onClick={subtract}>V</div>
                <div>{number}</div>
                <div className="increase" onClick={add(number)}>V</div>
            </div>
        </div>
    )


    return (
        <div>
            <div className="top">
                <input 
                    type='text'
                    value={input.itemName}
                    placeholder='Add an item'
                    name="itemName"
                    onChange={update}
                    className="input"
                />
                <span onClick={addToCart} className="list-plus">+</span>
            </div>
            {body}
        </div>
    )
}
