import React from "react";
import Body from "./Body";

export default function New(){
    const [input, setInput] = React.useState("")

    function update(event){
        setInput(prevValue => {
            prevValue=event.target.value
            console.log(prevValue)
        })
    }
    <Body add={input}/>


    return (
        <div>
            <input 
                type='text'
                value={input}
                placeholder='Add an item'
                name="input"
                onChange={update}
            />
            <span>+</span>
        </div>
    )
}
