import React from "react";
import Body from "./Body";

export default function New(){
    const [input, setInput] = React.useState("")
    const [items, setItems] = React.useState([])


    function update(event){
        setInput(event.target.value)
    }

    function addToCart(){
        setItems((previous) => [...previous,{tag: input,init: 1}])
    }

    const body = items.map(item => <Body 
        item={item.tag}
        initialVal={item.init}
    />)

    // const listItems = list.map((item) => {
    //     <div><span className="circle"></span> <span className="number">hello</span></div>
    // })


    return (
        <div>
            <input 
                type='text'
                value={input}
                placeholder='Add an item'
                name="input"
                onChange={update}
            />
            <span onClick={addToCart} className="list-plus">+</span>
            {/* <div>{input}</div> */}
            {body}
        </div>
    )
}
